import React from 'react';

const PaymentQrCode = () => {
    return (
        <div className="bg-blue-800 h-screen flex flex-col items-center justify-center p-4">
          <div className="bg-blue-900 rounded-lg p-6 text-white text-center shadow-xl w-full max-w-sm">
            <h2 className="text-2xl font-bold mb-6 flex items-center justify-center">
              <span className="mr-2">ðŸ’¸</span> {/* Replace with your payment icon */}
              Make Your Payment
            </h2>
            <div className="border-t border-gray-400 flex-grow mr-3 gap-7"></div>
            <div className="bg-white p-6 rounded-lg mb-6">
              {/* Static placeholder for the QR code. Replace with the actual QR code image */}
              <img src="/path-to-your-qr-code.png" alt="QR Code" className="mx-auto" />
            </div>
            <div className="flex items-center justify-center mb-6">
              <div className="border-t border-gray-400 flex-grow mr-3"></div>
              <div className="text-gray-400 uppercase">or</div>
              <div className="border-t border-gray-400 flex-grow ml-3"></div>
            </div>
            <button className="underline rounded py-2 px-4 text-lg font-bold transition-colors">
              Make Payment Manually
            </button>
            <div className="loader mt-6"></div>
          </div>
    
          {/* Simple Loader Animation */}
          <style>
            {`
              .loader {
                border-top-color: #3498db;
                -webkit-animation: spinner 1.5s linear infinite;
                animation: spinner 1.5s linear infinite;
              }
    
              @-webkit-keyframes spinner {
                0% { transform: rotate(0deg); }
                100% { transform: rotate(360deg); }
              }
    
              @keyframes spinner {
                0% { transform: rotate(0deg); }
                100% { transform: rotate(360deg); }
              }
            `}
          </style>
        </div>
      );
};

export default PaymentQrCode;
