import React, { useState } from 'react';

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errors, setErrors] = useState({});

  const handleSubmit = (e) => {
    e.preventDefault();
    let currentErrors = {};

    // Simple front-end validation checks
    if (!email) {
      currentErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(email)) {
      currentErrors.email = 'Email address is invalid';
    }

    if (!password) {
      currentErrors.password = 'Password is required';
    } else if (password.length < 8) {
      currentErrors.password = 'Password must be at least 8 characters long';
    }

    setErrors(currentErrors);

    // If there are no errors, submit the form
    if (Object.keys(currentErrors).length === 0) {
      console.log('Login with:', email, password);
      // Implement your login logic here, like calling an API
    }
  };

  return (
    <div className="bg-blue-800 h-screen flex items-center justify-center p-4">
      <div className="bg-blue-900 rounded-lg p-8 w-full max-w-sm">
        <h2 className="text-white text-2xl font-bold mb-6 flex items-center">
          {/* Replace this span with your actual icon */}
          <span className="mr-2">ðŸš€</span> Start Your Journey
        </h2>
        <form onSubmit={handleSubmit} className="flex flex-col gap-4">
          <div>
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className={`p-2 rounded text-gray-700 ${errors.email ? 'border-red-500' : ''}`}
            />
            {errors.email && <p className="text-red-500 text-xs italic">{errors.email}</p>}
          </div>
          <div>
            <input
              type="password"
              name="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              minLength="8"
              className={`p-2 rounded text-gray-700 ${errors.password ? 'border-red-500' : ''}`}
            />
            {errors.password && <p className="text-red-500 text-xs italic">{errors.password}</p>}
          </div>
          <button
            type="submit"
            className="bg-purple-600 text-white font-bold py-2 px-4 rounded hover:bg-purple-700"
          >
            Log In
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
