import React from 'react';

const offers = [
  { amount: 100, cardNumber: '1234 1234 1234 1234', name: 'ODEAN SMITH' },
  { amount: 300, cardNumber: '1234 1234 1234 1234', name: 'ODEAN SMITH' },
  { amount: 5000, cardNumber: '1234 1234 1234 1234', name: 'ODEAN SMITH' },
  // ... You can add more offers here
];

const OfferCard = ({ amount, cardNumber, name }) => {
  return (
    <div className="bg-white rounded-lg shadow-lg p-6 flex flex-col items-center w-64 m-4">
      {/* You will replace "path-to-logo" and "path-to-flag" with your actual image paths */}
      <img src="path-to-logo" alt="Logo" className="mb-3" />
      <img src="path-to-flag" alt="Country Flag" className="mb-3" />
      <div className="text-center">
        <div className="text-gray-700 text-sm">{cardNumber}</div>
        <div className="text-lg font-bold">{name}</div>
        <div className="text-xl font-bold text-green-500">${amount}</div>
      </div>
    </div>
  );
};

const OfferCards = () => {
  return (
    <div className="bg-blue-800 h-screen flex flex-col items-center p-4">
      <h2 className="text-white text-2xl font-bold mb-6">Select Your Offer</h2>
      <div className="flex overflow-x-auto">
        {offers.map((offer, index) => (
          <OfferCard key={index} amount={offer.amount} cardNumber={offer.cardNumber} name={offer.name} />
        ))}
      </div>
    </div>
  );
};

export default OfferCards;
